import React from 'react';
import PropTypes from 'prop-types';

const Button = ({ label, onClick, type = 'button', disabled = false, ariaLabel, ...props }) => (
  <button
    className="button"
    onClick={onClick}
    type={type}
    disabled={disabled}
    aria-label={ariaLabel || label}
    {...props}
  >
    {label}
  </button>
);

Button.propTypes = {
  label: PropTypes.string.isRequired,       // Rótulo visível no botão
  onClick: PropTypes.func.isRequired,       // Função chamada ao clicar no botão
  type: PropTypes.oneOf(['button', 'submit', 'reset']),
  disabled: PropTypes.bool,                 // Se o botão está desativado
  ariaLabel: PropTypes.string,              // Rótulo acessível para leitores de tela
};

export default Button;
